using UnityEngine;[ExecuteAlways]public class PostProcessTint:MonoBehaviour{ [SerializeField] Color _tint= new Color(0.1f,0.05f,0.2f,0.2f); Material _mat; void OnEnable(){ if(_mat==null){ var sh=Shader.Find("Custom/FullscreenTint"); if(sh!=null) _mat=new Material(sh); } } void OnRenderImage(RenderTexture src, RenderTexture dst){ if(_mat==null){ Graphics.Blit(src,dst); return;} _mat.SetColor("_Tint",_tint); Graphics.Blit(src,dst,_mat); } }